CREATE DATABASE telecontrol    WITH    OWNER = postgres    ENCODING = 'UTF8'    LC_COLLATE = 'Portuguese_Brazil.1252'    LC_CTYPE = 'Portuguese_Brazil.1252'    TABLESPACE = pg_default    CONNECTION LIMIT = -1    IS_TEMPLATE = False;CREATE TABLE clientes(    idcliente integer NOT NULL DEFAULT nextval('clientes_idcliente_seq'::regclass),    nome character varying(200) COLLATE pg_catalog."default",    cpf character varying(20) COLLATE pg_catalog."default",    cep character varying(10) COLLATE pg_catalog."default",    endereco character varying(200) COLLATE pg_catalog."default",    bairro character varying(100) COLLATE pg_catalog."default",    cidade character varying(100) COLLATE pg_catalog."default",    estado character varying(30) COLLATE pg_catalog."default",    CONSTRAINT clientes_pkey PRIMARY KEY (idcliente));CREATE TABLE ordem(    idordem integer NOT NULL DEFAULT nextval('ordem_idordem_seq'::regclass),    idcliente integer,    idproduto integer,    data_abertura date,    numero_ordem character varying(30) COLLATE pg_catalog."default",    status character varying(10) COLLATE pg_catalog."default",    CONSTRAINT ordem_pkey PRIMARY KEY (idordem));CREATE TABLE produtos(    idproduto integer NOT NULL DEFAULT nextval('produtos_idproduto_seq'::regclass),    codigo character varying(30) COLLATE pg_catalog."default",    descricao character varying(300) COLLATE pg_catalog."default",    situacao character varying(10) COLLATE pg_catalog."default",    CONSTRAINT produtos_pkey PRIMARY KEY (idproduto));